import { Modal } from 'antd'
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import { getAxios } from '../api'
import Slider from 'react-slick'
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'
import { useNavigate } from 'react-router-dom'

function MyWord(props) {
  const { me } = useSelector((state) => state.user)
  const [word, setWord] = useState(null)
  const axios = getAxios()
  const [isModalVisible, setIsModalVisible] = useState(false)
  const [diary, setDiary] = useState(null)
  const [title, setTitle] = useState(null)
  const navigate = useNavigate()

  const settings = {
    dots: true,
    infinite: false,
    speed: 500,
    slidesToShow: 5,
    slidesToScroll: 5,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 3,
          slidesToScroll: 3,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 2,
          initialSlide: 2,
        },
      },
      {
        breakpoint: 480,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  }

  useEffect(() => {
    me !== null &&
      axios.get(`/diary/myword`, { params: { userId: me.userId } }).then((res) => {
        setWord(res.data)
      })
  }, [me])

  const showModal = (item) => {
    console.log('item', item)
    setTitle(item)
    axios.get(`/diary/searchword`, { params: { userId: me.userId, word: item } }).then((res) => {
      console.log('res.data', res.data)
      setDiary(res.data)
    })
    setIsModalVisible(true)
  }

  const handleOk = () => {
    setIsModalVisible(false)
  }

  const handleCancel = () => {
    setIsModalVisible(false)
  }

  const pageMove = (dno, e) => {
    navigate(`/diary/read/${dno}`)
  }

  return (
    <div>
      {word != null &&
        word.map((item, idx) => (
          <div
            key={idx}
            style={{
              width: '60px',
              height: '30px',
              textAlign: 'center',
              float: 'left',
            }}>
            <div
              onClick={(e) => {
                showModal(item, e)
              }}>
              {item}
            </div>
          </div>
        ))}
      {title != null && (
        <Modal
          title={title}
          visible={isModalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
          width={1000}>
          <Slider {...settings}>
            {diary != null &&
              diary.map((item) => (
                <div
                  style={{ cursor: 'pointer' }}
                  className="card"
                  onClick={(e) => {
                    pageMove(item.dno, e)
                  }}>
                  <div className="card-top">
                    <img src={item.image} alt="img" width={150} />
                  </div>
                  <div className="card-bottom">
                    <img
                      style={{ float: 'left', borderRadius: '50%', width: '50' }}
                      src={me.image}
                      alt="img"
                    />
                    <p style={{ float: 'left' }}>{item.nickname}</p>
                  </div>
                </div>
              ))}
          </Slider>
        </Modal>
      )}
    </div>
  )
}

export default MyWord
