import { EditOutlined } from '@ant-design/icons'
import { Form } from 'antd'
import React, { useEffect, useState } from 'react'
import { useSelector } from 'react-redux'
import styled from 'styled-components'
import ImageFileInput from './ImageFileInput'
import MyGender from './Profile/MyGender'
import MyNickName from './Profile/MyNickname'

const MyPic = styled.div`
  margin-left: auto;
  margin-right: auto;
  width: 250px;
  height: 250px;
  border-radius: 50%;
  border: solid;
  overflow: hidden;
  position: relative;
`

const MyNick = styled.div`
  margin-top: 10%;
`

const Gender = styled.div`
  margin-top: 5%;
`

const Age = styled.div`
  margin-top: 5%;
`

function ProfileForm({ imageUploader }) {
  const { me } = useSelector((state) => state.user)
  const [file2, setFile2] = useState({ fileName: null, fileURL: null })
  const [editable, setEditable] = useState(false)

  const checkGender = () => {
    if (me != null && me.gender === 'male') {
      return '남'
    } else {
      return '여'
    }
  }

  return (
    <div>
      <Form>
        <Form.Item>
          <MyPic>{me !== null && <img src={me.image} alt="img" width={'100%'} />}</MyPic>
          <MyNick>닉네임 : {me !== null && me.nickname}</MyNick>
          <Gender>성별 : {checkGender()}</Gender>
          {editable === false ? (
            <Age>
              나이 : {me !== null && me.age}{' '}
              <EditOutlined
                onClick={(e) => {
                  setEditable(!editable)
                }}></EditOutlined>
            </Age>
          ) : (
            <Age>
              나이 :
              <Form
                form={form}
                name="modify"
                onFinish={onFinish}
                autoComplete="off"
                layout="vertical">
                <Form.Item
                  label="연령대"
                  name="ageRange"
                  rules={[
                    {
                      required: false,
                      message: 'Please select ageRange!',
                    },
                  ]}>
                  <Select placeholder="Select your ageRange">
                    <Option value="1">어린이 (0~9)</Option>
                    <Option value="2">청소년 (10~19)</Option>
                    <Option value="3">청년 (20~29)</Option>
                    <Option value="4">중/장년 (30~59)</Option>
                    <Option value="5">노년 (60~)</Option>
                  </Select>
                </Form.Item>
                <Form.Item>
                  <Button type="primary" htmlType="submit" onClick={success}>
                    Register
                  </Button>
                </Form.Item>
              </Form>
              <EditOutlined
                onClick={(e) => {
                  setEditable(!editable)
                }}></EditOutlined>
            </Age>
          )}
        </Form.Item>
      </Form>
    </div>
  )
}

export default ProfileForm
