import { Spin } from 'antd'
import React, { useRef, useState } from 'react'
import { useSelector } from 'react-redux'
import styled from 'styled-components'

function ImageFileInput({ imageUploader, onFileChange, file, data }) {
  const { me } = useSelector((state) => state.user)

  const inputRef = useRef()
  const [loading, setLoading] = useState(false)
  const { fileURL } = data
  const onButtonClick = (e) => {
    e.preventDefault()
    inputRef.current.click()
  }

  const onChange = async (e) => {
    setLoading(true)
    const uploaded =
      e.target.files[0] !== undefined && (await imageUploader.upload(e.target.files[0]))
    setLoading(false)
    onFileChange({
      name: uploaded.original_filename,
      url: uploaded.url,
    })
  }

  return (
    <StyledImageArea className="styled-image-area">
      <input
        style={{ display: 'none' }}
        ref={inputRef}
        type="file"
        accept="image/*"
        name="file"
        onChange={onChange}
      />

      {!loading && (
        <button
          style={{
            width: '100%',
            height: '100%',
            backgroundColor: '#ffff',
            border: 'none',
            cursor: 'pointer',
            padding: '0',
          }}
          onClick={onButtonClick}>
          <img
            src={file.fileURL || fileURL || me.image}
            alt="img"
            width="100%"
            height="100%"
            style={{ objectFit: 'cover', verticalAlign: 'top' }}
          />
        </button>
      )}
      {loading && (
        <div
          style={{
            width: '100%',
            textAlign: 'center',
            // lineHeight: '250px'
          }}>
          <Spin size="large" tip="Loading..." />
        </div>
      )}
    </StyledImageArea>
  )
}

const StyledImageArea = styled.div`
  width: 100%;
  height: 100%;
  overflow: hidden;
  // border: 1px solid blue;
  border-radius: 10px;
`

export default ImageFileInput
