import React, { useRef, useState } from 'react'
import styled from 'styled-components'
import { Row, Col, Button } from 'antd'
import ImageFileInput from '../components/ImageFileInput'
import { useDispatch, useSelector } from 'react-redux'
import { articleAddRequestAction } from '../reducers/article'
import { Upload, message } from 'antd'
import { InboxOutlined } from '@ant-design/icons'
import Dragger from 'antd/lib/upload/Dragger'

const Word = styled.h1`
  margin: 10px auto;
  width: 150px;
  background-color: #ffff;
`

const ImageLayout = styled.div`
  border: 1px solid blue;
  margin-right: 10px;
  background-color: #ffff;
  height: 30vh;
  border-radius: 20px;
`
const WritingLayout = styled.div`
  border: 1px solid red;
  background-color: #ffff;
`

const Submit = styled(Button)`
  margin-top: 10px;
`

const Side = styled.div`
  background-color: #fcfcf8;
  outline: 1px solid transparent;
  height: 30vh;
  &:nth-of-type(1) {
    padding: 2rem 2rem 0;
    box-shadow: inset 0 0.75rem 2rem rgba(229, 225, 187, 0.5);
  }
`

const P = styled.p`
  margin: 0;
  padding: 0;
`

const Textarea = styled.textarea`
  line-height: 1.5rem;
  border: 0;
  outline: none;
  font-family: inherit;
  appearance: none;
  color: #4e5e72;
  background-color: transparent;
  background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='10' height='24'><rect fill='rgb(229, 225, 187)' x='0' y='23' width='10' height='1'/></svg>");
  width: 100%;
  height: 20vh;
  resize: none;

  &: focus {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='10' height='24'><rect fill='rgba(78, 94, 114, 0.3)' x='0' y='23' width='10' height='1'/></svg>");
    outline: none;
  } ;
`

const MyDragger = styled(Dragger)`
  > .ant-upload ant-upload-drag sc-hbyLVd etlWvI {
    width: 200px;
  }
`

function ArticleForm({ imageUploader, data }) {
  const { me } = useSelector((state) => state.user)
  const dispatch = useDispatch()
  const formRef = useRef()
  const messageRef = useRef()
  const [file, setFile] = useState({ fileName: null, fileURL: null })
  const { word, message } = data
  const onFileChange = (file) => {
    setFile({
      fileName: file.name,
      fileURL: file.url,
    })
  }

  const onSubmit = (event) => {
    event.preventDefault()
    const data = {
      content: messageRef.current.value || '',
      word: word,
      image: file.fileURL || '',
    }
    dispatch(articleAddRequestAction({ userId: me.userId, data }))
    formRef.current.reset()
  }

  const { Dragger } = Upload

  const props = {
    name: 'file',
    multiple: true,
    action: 'https://www.mocky.io/v2/5cc8019d300000980a055e76',
    onChange(info) {
      const { status } = info.file
      if (status !== 'uploading') {
        console.log(info.file, info.fileList)
      }
      if (status === 'done') {
        message.success(`${info.file.name} file uploaded successfully.`)
      } else if (status === 'error') {
        message.error(`${info.file.name} file upload failed.`)
      }
    },
    onDrop(e) {
      console.log('Dropped files', e.dataTransfer.files)
    },
  }

  return (
    <form
      ref={formRef}
      style={{ width: '80%', margin: '0 auto', backgroundColor: '#EEA7BB', padding: '20px' }}>
      <Word>{word}</Word>
      <Row>
        <Col span={8}>
          <ImageLayout>
            {/* <ImageFileInput
              name={file.fileName}
              imageUploader={imageUploader}
              onFileChange={onFileChange}
              file={file}
              data={data}
              setFile={setFile}
            /> */}
            <MyDragger {...props}>
              <p className="ant-upload-drag-icon">
                <InboxOutlined />
              </p>
              <p className="ant-upload-text">Click or drag file to this area to upload</p>
            </MyDragger>
          </ImageLayout>
        </Col>
        <Col span={16}>
          <WritingLayout>
            <Side>
              <P>
                <Textarea ref={messageRef} name="message" placeholder={message}></Textarea>
              </P>
            </Side>
          </WritingLayout>
        </Col>
      </Row>
      <Submit type="primary" onClick={onSubmit}>
        등록
      </Submit>
    </form>
  )
}

export default ArticleForm
