import React, { useEffect, useRef, useState } from 'react'
import styled from 'styled-components'
import { Row, Col, Button } from 'antd'
import ImageFileInput from '../../components/ImageFileInput'
import { useDispatch, useSelector } from 'react-redux'
import { articleAddRequestAction } from '../../reducers/article'
import { Upload, message } from 'antd'
import { useParams, useNavigate } from 'react-router-dom'
import { getAxios } from '../../api'
import ImageUploader from '../../service/image_uploader'

const { Dragger } = Upload

const imageUploader = new ImageUploader()

const Word = styled.h1`
  margin: 10px auto;
  width: 150px;
  background-color: #ffff;
`

const ImageLayout = styled.div`
  margin-right: 10px;
  background-color: #ffff;
  height: 30vh;
`
const WritingLayout = styled.div`
  border: 1px solid red;
  background-color: #ffff;
  height: 150px;
`

const Submit = styled(Button)`
  margin-top: 10px;
`

function ModifyForm({ imageUploader, data }) {
  const axios = getAxios()
  const { me } = useSelector((state) => state.user)
  const { articleAddDone } = useSelector((state) => state.article)
  const navigate = useNavigate()
  const dispatch = useDispatch()
  const formRef = useRef()
  const messageRef = useRef()
  const { word, message, fileURL } = data
  const [file, setFile] = useState({ fileName: null, fileURL: fileURL })
  const [content, setContent] = useState('')
  const { dno } = useParams()

  const onFileChange = (file) => {
    setFile({
      fileName: file.name,
      fileURL: file.url,
    })
  }

  const onSubmit = (event) => {
    event.preventDefault()
    // const data = {
    //   content: messageRef.current.value || '',
    //   word: word,
    //   image: file.fileURL || '',
    // }
    // dispatch(articleAddRequestAction({ userId: me.userId, data, navigate }))
    // formRef.current.reset()
    try {
      axios.put(
        `diary/${dno}`,
        {
          content: content,
          image: file.fileURL,
        },
        {
          params: { dno: dno, userId: me.userId },
        },
      )
      console.log('완료')
    } catch (err) {
      console.log(err)
    }
  }

  const onChange = (e) => {
    console.log('e.target.value: ', e.target.value)
    setContent(e.target.value)
  }

  return (
    <form
      ref={formRef}
      style={{ width: '80%', margin: '0 auto', backgroundColor: '#EEA7BB', padding: '20px' }}>
      <Word>{word}</Word>
      <Row>
        <Col span={8}>
          <ImageLayout>
            <div
              style={{
                width: '100%',
                height: '100%',
                backgroundColor: '#fff',
                border: 'none',
              }}>
              <ImageFileInput
                name={file.fileName}
                imageUploader={imageUploader}
                onFileChange={onFileChange}
                file={file}
                data={data}
                setFile={setFile}
              />
            </div>
          </ImageLayout>
        </Col>
        <Col span={16}>
          <WritingLayout>
            {/* <input
              type="text"
              name="message"
              ref={messageRef}
              defaultValue={message}
              onChange={onChange}>
              {message}
            </input> */}
            <textarea ref={messageRef} name="message" defaultValue={message}></textarea>
          </WritingLayout>
        </Col>
      </Row>
      <Submit type="primary" onClick={onSubmit}>
        등록
      </Submit>
    </form>
  )
}

export default ModifyForm
