import React, { useEffect, useState } from 'react'
import { Row, Col, Typography, Avatar, Image } from 'antd'
import { UserOutlined } from '@ant-design/icons'
import styled from 'styled-components'
import { useDispatch, useSelector } from 'react-redux'
import moment from 'moment'
import { BsHeart, BsHeartFill } from 'react-icons/bs'
import { getAxios } from '../../api'
import { useParams } from 'react-router-dom'
import jwt_decode from 'jwt-decode'
import { loadUserRequestAction } from '../../reducers/user'

const { Title, Text } = Typography

function DiaryHeader({ diary }) {
  const [word, setWord] = useState('')
  const [user, setUser] = useState('')
  const [date, setDate] = useState('')
  const { me } = useSelector((state) => state.user)
  const [state, setState] = useState(false)
  const axios = getAxios()
  const params = useParams()
  const dispatch = useDispatch()

  const onChangeLiked = () => {
    if (state) {
      setState(false)
    } else {
      setState(true)
      axios.post('/liked', { dno: params.dno, userId: me.userId }).then((res) => {
        console.log(res.data.liked.dno)
        axios.get('/liked', { params: { dno: res.data.liked.dno } }).then((res) => {
          console.log(res)
          const decode_token = jwt_decode(localStorage.getItem('jwtToken'))
          const userId = decode_token.sub
          dispatch(loadUserRequestAction({ userId }))
        })
      })
    }
  }

  useEffect(() => {
    console.log('me', me)
  })

  return (
    <Row>
      <Col span={12}>
        <StyledWordTitle level={3}>#{diary.word}</StyledWordTitle>
        {state ? (
          <>
            <StyledUnLiked onClick={onChangeLiked} />
            {diary.liked}
          </>
        ) : (
          <>
            <StyledLiked onClick={onChangeLiked} />
            {diary.liked}
          </>
        )}
        <div>조회수 : {diary.view}</div>
      </Col>
      <Col span={12}>
        <StyledUserArea>
          {me !== null ? (
            me.image === null ? (
              <Avatar icon={<UserOutlined />} />
            ) : (
              <Avatar src={<Image src={me.image} style={{ width: 32 }} />} />
            )
          ) : null}
          <Title level={5}>{me !== null && me.nickname}</Title>
        </StyledUserArea>
        <StyledDateText level={5}>
          {moment(diary.createdat).format('YYYY-MM-DD HH:mm:ss')}
        </StyledDateText>
      </Col>
    </Row>
  )
}

const StyledWordTitle = styled(Title)`
  display: flex;
  justify-content: start;
  align-items: center;
  // margin: 1.2em 1em 1em 1em;
  padding: 0.4em 0.2em 0.2em 0.2em;
`

const StyledUserArea = styled.div`
  display: flex;
  justify-content: end;
  align-items: center;
`

const StyledDateText = styled(Text)`
  display: flex;
  justify-content: end;
  align-items: center;
`

const StyledLiked = styled(BsHeart)`
  cursor: pointer;
`

const StyledUnLiked = styled(BsHeartFill)`
  cursor: pointer;
`

export default DiaryHeader
