import React from 'react'
import styled from 'styled-components'

const Small = styled.small`
  display: block;
  padding: 1rem 0;
  font-size: 0.8rem;
  transition: opacity 0.33s;
`

const H1 = styled.h1`
  margin: 0;
  padding: 0;
  font-size: 2rem;
  font-family: 'Dancing Script';
`

const Textarea = styled.textarea`
  line-height: 1.5rem;
  border: 0;
  outline: none;
  font-family: inherit;
  appearance: none;
  color: #4e5e72;
  background-color: transparent;
  background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='10' height='24'><rect fill='rgb(229, 225, 187)' x='0' y='23' width='10' height='1'/></svg>");
  width: 100%;
  height: 8rem;
  resize: none;
  &:focus {
    background-image: url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='10' height='24'><rect fill='rgba(78, 94, 114, 0.3)' x='0' y='23' width='10' height='1'/></svg>");
    outline: none;
  }
`

const Button = styled.button`
  line-height: 1.5rem;
  border: 0;
  outline: none;
  font-family: inherit;
  appearance: none;
  padding: 0.5rem 1rem;
  border-radius: 0.25rem;
  background-color: rgba(78, 94, 114, 0.9);
  color: white;
  font-size: 1rem;
  transition: background-color 0.2s;
  &:hover,
  :focus {
    outline: none;
    background-color: rgba(78, 94, 114, 1);
  }
`

const Side = styled.div``

function Test(props) {
  return (
    <div>
      <Small>Enter message (optional) and click button "Send"</Small>
      <div style={{ width: '35rem', backgroundColor: 'white' }}>
        <article
          style={
            {
              // position: 'absolute',
              // left: '0',
              // right: '0',
              // top: '0',
              // width: '30rem',
              // margin: 'auto',
              // perspective: '60rem',
            }
          }>
          <Side>
            <H1>Contact us</H1>
            <p>
              <Textarea placeholder="Your message"></Textarea>
            </p>
          </Side>
          <div class="side">
            <p>
              <Button id="sendLetter">Send</Button>
            </p>
          </div>
        </article>
      </div>
      <p class="result-message centered">Thank you for your message</p>
    </div>
  )
}

export default Test
