import { getAxios } from '../api'
import { all, fork, takeLatest, call, put, delay } from 'redux-saga/effects'
import { ARTICLE_ADD_FAILURE, ARTICLE_ADD_REQUEST, ARTICLE_ADD_SUCCESS } from '../reducers/article'

const axios = getAxios()

function articleAddAPI(data) {
  console.log('data', data)
  return axios.post(`dairy/${data.userId}`, data)
}

function* articleAdd(action) {
  try {
    const result = yield call(articleAddAPI, action.data)
    yield put({
      type: ARTICLE_ADD_SUCCESS,
      data: action.data,
    })
  } catch (err) {
    yield put({
      type: ARTICLE_ADD_FAILURE,
      error: err.response.data,
    })
  }
}

function* watchArticleAdd() {
  yield takeLatest(ARTICLE_ADD_REQUEST, articleAdd)
}

export default function* userSaga() {
  yield all([fork(watchArticleAdd)])
}
