import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import { List, message, Avatar } from 'antd'
import VirtualList from 'rc-virtual-list'
import { getAxios } from '../../api'
import { EyeFilled, HeartFilled, MessageFilled } from '@ant-design/icons'
import { useNavigate } from 'react-router-dom'

const MainLook = styled.div`
  margin-top: 5%;
  background-color: pink;
`
const fakeDataUrl = 'https://randomuser.me/api/?results=20&inc=name,gender,email,nat,picture&noinfo'
const ContainerHeight = 400

function DiaryList(props) {
  const [data, setData] = useState([])
  const axios = getAxios()
  const navigate = useNavigate()

  const appendData = () => {
    // fetch(fakeDataUrl)
    //   .then((res) => res.json())
    //   .then((body) => {
    //     setData(data.concat(body.results))
    //     message.success(`${body.results.length} more items loaded!`)
    //   })
    axios.get('/diary/').then((res) => {
      console.log('res', res.data.dairies)
      setData(res.data.dairies)
    })
  }

  useEffect(() => {
    appendData()
  }, [])

  const onScroll = (e) => {
    if (e.target.scrollHeight - e.target.scrollTop === ContainerHeight) {
      appendData()
    }
  }

  const pageMove = (dno, e) => {
    axios.get('/diary/view', { params: { dno: dno } }).then(() => {
      navigate(`/diary/read/${dno}`)
    })
  }

  return (
    <div>
      <MainLook>
        <List>
          <VirtualList data={data} height={ContainerHeight}>
            {(item) => (
              <List.Item
                key={item.dno}
                onClick={(e) => {
                  pageMove(item.dno, e)
                }}>
                <List.Item.Meta
                  avatar={
                    <>
                      <Avatar src={item.image} />
                      <span>{item.nickname}</span>
                    </>
                  }
                  title={<div>#{item.word}</div>}
                  description={
                    <p>
                      {item.content.length >= 50
                        ? item.content.substr(0, 50) + '...'
                        : item.content}
                    </p>
                  }
                />
                <div>
                  <div>
                    <EyeFilled /> {item.view}
                  </div>
                  <div>
                    <HeartFilled /> {item.liked}
                  </div>
                  <div>
                    <MessageFilled /> 0
                  </div>
                </div>
              </List.Item>
            )}
          </VirtualList>
        </List>
      </MainLook>
    </div>
  )
}

export default DiaryList
